<script>
document.addEventListener('DOMContentLoaded', function() {
  const contactForm = document.querySelector('[data-contact-form]');
  if (!contactForm) return;

  const successMessage = contactForm.querySelector('[data-success-message]');
  const errorMessage = contactForm.querySelector('[data-error-message]');

  contactForm.addEventListener('submit', function(e) {
    e.preventDefault();

    // Clear previous messages
    successMessage.classList.add('hidden');
    errorMessage.classList.add('hidden');

    // Basic validation
    const name = this.querySelector('#contact-name').value.trim();
    const email = this.querySelector('#contact-email').value.trim();
    const message = this.querySelector('#contact-message').value.trim();

    if (!name || !email || !message) {
      errorMessage.textContent = 'Please fill in all required fields.';
      errorMessage.classList.remove('hidden');
      return;
    }

    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      errorMessage.textContent = 'Please enter a valid email address.';
      errorMessage.classList.remove('hidden');
      return;
    }

    // Simulate form submission
    const submitButton = this.querySelector('button[type="submit"]');
    const originalText = submitButton.textContent;
    submitButton.textContent = 'Sending...';
    submitButton.disabled = true;

    setTimeout(() => {
      successMessage.classList.remove('hidden');
      this.reset();
      submitButton.textContent = originalText;
      submitButton.disabled = false;

      // Scroll to success message
      successMessage.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
    }, 1500);
  });
});
</script>

